import views
from django.conf import settings
from django.conf.urls import patterns, include, url
from django.contrib.staticfiles.urls import staticfiles_urlpatterns
from tastypie.api import Api

from wines.api import WineResource, WineProducerResource
# Uncomment the next two lines to enable the admin:
# from django.contrib import admin
# admin.autodiscover()

wine_resource = WineResource()
wine_producer_resource = WineProducerResource()

#curl http://127.0.0.1:8000/api/v1/ to see API description
v1_api = Api(api_name='v1')
v1_api.register(WineProducerResource())
v1_api.register(WineResource())

urlpatterns = patterns('',
    url(r'^$', views.root),
    url(r'^about/$', views.about),
    url(r'^search/$', views.search),
    url(r'^api/', include(v1_api.urls)),
    # Examples:
    # url(r'^$', 'wine_tour.views.home', name='home'),
    # url(r'^wine_tour/', include('wine_tour.foo.urls')),

    # Uncomment the admin/doc line below to enable admin documentation:
    # url(r'^admin/doc/', include('django.contrib.admindocs.urls')),

    # Uncomment the next line to enable the admin:
    # url(r'^admin/', include(admin.site.urls)),
)

#For serving static files from Django server. NOT RECOMMENDED FOR PRODUCTION
urlpatterns += staticfiles_urlpatterns()
